'use client';

import { useState } from 'react';
import { Investigacion } from '@/types/investigacion';
import { storageService } from '@/lib/storage';

interface SearchModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSelect: (investigacion: Investigacion) => void;
}

export default function SearchModal({ isOpen, onClose, onSelect }: SearchModalProps) {
  const [searchBy, setSearchBy] = useState<'id' | 'nombre'>('nombre');
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState<Investigacion[]>([]);

  if (!isOpen) return null;

  const handleSearch = () => {
    if (searchTerm.trim()) {
      const found = storageService.search(searchTerm, searchBy);
      setResults(found);
    }
  };

  const handleSelect = (investigacion: Investigacion) => {
    onSelect(investigacion);
    onClose();
    setSearchTerm('');
    setResults([]);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-2xl max-h-[80vh] overflow-auto">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold">Consultar Folio</h2>
          <button
            onClick={onClose}
            className="text-gray-500 hover:text-gray-700 text-2xl"
          >
            ×
          </button>
        </div>

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium mb-2">Buscar por:</label>
            <div className="flex gap-4">
              <label className="flex items-center">
                <input
                  type="radio"
                  value="nombre"
                  checked={searchBy === 'nombre'}
                  onChange={(e) => setSearchBy(e.target.value as 'nombre')}
                  className="mr-2"
                />
                Nombre
              </label>
              <label className="flex items-center">
                <input
                  type="radio"
                  value="id"
                  checked={searchBy === 'id'}
                  onChange={(e) => setSearchBy(e.target.value as 'id')}
                  className="mr-2"
                />
                ID
              </label>
            </div>
          </div>

          <div>
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              placeholder={`Buscar por ${searchBy}...`}
              className="w-full border border-gray-300 rounded px-3 py-2"
            />
          </div>

          <button
            onClick={handleSearch}
            className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
          >
            Buscar
          </button>

          {results.length > 0 && (
            <div className="mt-4">
              <h3 className="font-semibold mb-2">Resultados ({results.length}):</h3>
              <div className="space-y-2">
                {results.map((item) => (
                  <div
                    key={item.id}
                    onClick={() => handleSelect(item)}
                    className="p-3 border border-gray-200 rounded hover:bg-gray-50 cursor-pointer"
                  >
                    <div className="font-semibold">{item.nombre}</div>
                    <div className="text-sm text-gray-600">ID: {item.id}</div>
                    <div className="text-sm text-gray-600">Fecha: {item.fecha}</div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {searchTerm && results.length === 0 && (
            <div className="text-center text-gray-500 py-4">
              No se encontraron resultados
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Investigacion } from '@/types/investigacion';
import { storageService } from '@/lib/storage';
import SearchModal from '@/components/SearchModal';

export default function InvestigacionesForm() {
  const [formData, setFormData] = useState<Investigacion>({
    id: '',
    nombre: '',
    edad: '',
    area: '',
    antiguedad: '',
    declaracionAccidente: '',
    fecha: new Date().toISOString().split('T')[0]
  });

  const [isSearchModalOpen, setIsSearchModalOpen] = useState(false);
  const [isSaved, setIsSaved] = useState(false);

  useEffect(() => {
    // Generate new ID on component mount
    setFormData(prev => ({ ...prev, id: storageService.generateId() }));
  }, []);

  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Save to storage
    storageService.save(formData);

    // Download CSV
    storageService.downloadCSV(formData);

    // Show success message
    setIsSaved(true);
    setTimeout(() => setIsSaved(false), 3000);

    // Reset form with new ID
    setFormData({
      id: storageService.generateId(),
      nombre: '',
      edad: '',
      area: '',
      antiguedad: '',
      declaracionAccidente: '',
      fecha: new Date().toISOString().split('T')[0]
    });
  };

  const handleLoadInvestigacion = (investigacion: Investigacion) => {
    setFormData(investigacion);
  };

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white rounded-lg shadow-lg p-6">
        {isSaved && (
          <div className="mb-4 p-3 bg-green-100 text-green-700 rounded">
            ¡Investigación guardada exitosamente!
          </div>
        )}

        <div className="flex justify-between items-center mb-6">
          <div>
            <h2 className="text-2xl font-bold">Investigación</h2>
            <p className="text-sm text-gray-600">Folio: {formData.id}</p>
          </div>
          <button
            type="button"
            onClick={() => setIsSearchModalOpen(true)}
            className="bg-green-600 text-white py-2 px-4 rounded hover:bg-green-700"
          >
            Consultar Folio
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium mb-1">
                Fecha *
              </label>
              <input
                type="date"
                name="fecha"
                value={formData.fecha}
                onChange={handleInputChange}
                required
                className="w-full border border-gray-300 rounded px-3 py-2"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">
                Nombre *
              </label>
              <input
                type="text"
                name="nombre"
                value={formData.nombre}
                onChange={handleInputChange}
                required
                className="w-full border border-gray-300 rounded px-3 py-2"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">
                Edad *
              </label>
              <input
                type="text"
                name="edad"
                value={formData.edad}
                onChange={handleInputChange}
                required
                className="w-full border border-gray-300 rounded px-3 py-2"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">
                Área *
              </label>
              <input
                type="text"
                name="area"
                value={formData.area}
                onChange={handleInputChange}
                required
                className="w-full border border-gray-300 rounded px-3 py-2"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">
                Antigüedad *
              </label>
              <input
                type="text"
                name="antiguedad"
                value={formData.antiguedad}
                onChange={handleInputChange}
                required
                className="w-full border border-gray-300 rounded px-3 py-2"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium mb-1">
              Declaración de Accidente *
            </label>
            <textarea
              name="declaracionAccidente"
              value={formData.declaracionAccidente}
              onChange={handleInputChange}
              required
              rows={6}
              className="w-full border border-gray-300 rounded px-3 py-2"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-3 px-4 rounded hover:bg-blue-700 font-semibold"
          >
            Grabar
          </button>
        </form>
      </div>

      <SearchModal
        isOpen={isSearchModalOpen}
        onClose={() => setIsSearchModalOpen(false)}
        onSelect={handleLoadInvestigacion}
      />
    </div>
  );
}

