'use client';

import { useState, useEffect } from 'react';
import { Investigacion, AccionCorrectiva } from '@/types/investigacion';
import { storageService } from '@/lib/storage';
import SearchModal from '@/components/SearchModal';
import AccionesCorrectivas from '@/components/AccionesCorrectivas';

export default function InvestigacionesForm() {
  const [formData, setFormData] = useState<Investigacion>({
    id: '000000',
    nombre: '',
    edad: '',
    area: '',
    antiguedad: '',
    declaracionAccidente: '',
    fecha: new Date().toISOString().split('T')[0],
    acciones: []
  });

  const [isSearchModalOpen, setIsSearchModalOpen] = useState(false);
  const [isSaved, setIsSaved] = useState(false);
  const [showOtroAreaInput, setShowOtroAreaInput] = useState(false);
  const [isUpdate, setIsUpdate] = useState(false);
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);
    // Generate new ID on component mount (client-side only)
    if (typeof window !== 'undefined') {
      setFormData(prev => ({ ...prev, id: storageService.generateId() }));
    }
  }, []);

  const areas = [
    'Embalaje A',
    'Vigilancia',
    'Bodega de Despacho',
    'Bodega de Granel',
    'Mantención',
    'Embalaje B',
    'Galvanoplastía',
    'Control de Calidad',
    'Laminado',
    'Cabeceado',
    'RRHH',
    'Reponedores',
    'Vendedores Terreno',
    'Atención clientes',
    'Facturación',
    'Admin 2ndo piso',
    'Informática',
    'Otros'
  ];

  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;

    if (name === 'area') {
      setShowOtroAreaInput(value === 'Otros');
      if (value !== 'Otros') {
        setFormData(prev => ({ ...prev, [name]: value }));
      } else {
        setFormData(prev => ({ ...prev, [name]: '' }));
      }
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Save to storage (update if existing, new if not)
    storageService.save(formData, isUpdate);

    // Download CSV of entire database
    storageService.downloadCSV();

    // Show success message
    setIsSaved(true);
    setTimeout(() => setIsSaved(false), 3000);

    // Reset form with new ID
    setFormData({
      id: storageService.generateId(),
      nombre: '',
      edad: '',
      area: '',
      antiguedad: '',
      declaracionAccidente: '',
      fecha: new Date().toISOString().split('T')[0],
      acciones: []
    });
    setShowOtroAreaInput(false);
    setIsUpdate(false);
  };

  const handleLoadInvestigacion = (investigacion: Investigacion) => {
    setFormData(investigacion);
    setIsUpdate(true);
