        }
      }
    }
  };

  return (
    <input
      ref={inputRef}
      type="text"
      name={name}
      value={displayValue}
      onChange={handleChange}
      onBlur={handleBlur}
      placeholder="dd/mm/yyyy"
      required={required}
      className={className}
      maxLength={10}
    />
  );
}

